import React from 'react';
import TabItem from './tabItem';
import AddIcon from '../assest/image/add-icon.jpeg';
import ThreeDotIcon from '../assest/image/three-dot-icon.jpeg';


const TabList = () => {
    const initialTabList = [
        {
            id: 1,
            method: 'GET',
            content: 'Unitled Request'   
        },
        {
            id: 2,
            method: 'POST',
            content: 'test'
        },
      ];
    const [tabList, setTabList] = React.useState(initialTabList);
    const [length, setLength] = React.useState(tabList.length);

    const onAddTab = () => {
        const newTab = {
            id: length + 1,
            method: 'GET',
            content: 'Unitled Request'
        }
        setTabList([...tabList, newTab]);
        setLength(length + 1);
    }

  return (
    <div className="tab-list-container">
      <ul>
        {tabList.map((tabItem,index) => {
            return (
                <TabItem 
                key={tabItem.id} 
                method={tabItem.method} 
                content={tabItem.content}
                handleCalbackDeleteTab={(id) => {
                    const newTabList = tabList.filter((item) => item.id !== id);
                    setTabList(newTabList);
                }}
                />
            );
        })}
      </ul>
      <div 
      className="add-tab"
      onClick={() => onAddTab()}
      >
        <img src={AddIcon} alt="add-icon" />
      </div>
      <div className="add-tab">
        <img src={ThreeDotIcon} alt="add-icon" />
      </div>
    </div>

  );
}

export default TabList;
