import React from 'react';
import DeleteIcon from '../assest/image/delete-icon.png';


const TabItem = (props) => {
  const { method, content, handleCalbackDeleteTab } = props;

  const onDeleteTab = () => {
    handleCalbackDeleteTab();
  }

  // //get element by id
  // const tabItem = document.getElementById('tab-item');
  // const width = tabItem.offsetWidth;

  const getTabItemWidth = () => {
    const tabItem = document.getElementById('tab-item');
    const width = tabItem.offsetWidth;
    return width;
  }

  const contentFixed = (orginalContent) => {
    console.log(getTabItemWidth());
    if (orginalContent.length > 10) {
      return orginalContent.slice(0, 10) + '...';
    }
    return orginalContent;
  }

  return (
    <div className="tab-item-container">
      <div className="method">{method}</div>
      <div className="content">
        {contentFixed(content)}
      </div>
      <div
        className="delete-icon"
        onClick={() => onDeleteTab()}
      >
        <img src={DeleteIcon} alt="delete-icon" />
      </div>
    </div>
  );
}

export default TabItem;
